import { z } from 'zod';

import { saveGameEnvelopeSchema } from '../schemas.ts';
import { fmtNum } from '../../util/format.ts';

export interface SaveGameMigrationStep {
  readonly fromVersion: number;
  readonly toVersion: number;
  readonly migrate: (input: unknown) => unknown | Promise<unknown>;
}

const schemaVersionSchema = saveGameEnvelopeSchema.extend({
  schemaVersion: z.number().int().nonnegative(),
});

function extractSchemaVersion(payload: unknown): number {
  const parsed = schemaVersionSchema.safeParse(payload);

  if (!parsed.success) {
    throw new Error('Save game payload is missing a numeric schemaVersion');
  }

  return parsed.data.schemaVersion;
}

export class SaveGameMigrationRegistry {
  private readonly steps = new Map<number, SaveGameMigrationStep>();

  public constructor(private readonly targetVersion: number) {}

  public register(step: SaveGameMigrationStep): void {
    if (this.steps.has(step.fromVersion)) {
      throw new Error(`Migration from version ${fmtNum(step.fromVersion)} already registered`);
    }

    if (step.toVersion <= step.fromVersion) {
      throw new Error('Migration steps must increase schemaVersion');
    }

    this.steps.set(step.fromVersion, step);
  }

  public async migrate(payload: unknown, targetVersion = this.targetVersion): Promise<unknown> {
    let working = payload;
    let version = extractSchemaVersion(working);

    if (version > targetVersion) {
      throw new Error(`Cannot migrate save from newer schemaVersion ${fmtNum(version)}`);
    }

    while (version < targetVersion) {
      const step = this.steps.get(version);

      if (!step) {
        throw new Error(`No migration registered for schemaVersion ${fmtNum(version)}`);
      }


      working = await step.migrate(working);
      version = extractSchemaVersion(working);

      if (version !== step.toVersion) {
        throw new Error(
          `Migration from ${fmtNum(step.fromVersion)} returned schemaVersion ${fmtNum(version)}; expected ${fmtNum(step.toVersion)}`,
        );
      }
    }

    return working;
  }
}

export function cloneTelemetryPayload(payload: Record<string, unknown>): Record<string, unknown> {
  if (typeof structuredClone === 'function') {
    return structuredClone(payload);
  }

  return JSON.parse(JSON.stringify(payload));
}

import { describe, expect, it } from 'vitest';

import { HOURS_PER_TICK } from '@/backend/src/constants/simConstants';
import { runTick, type EngineRunContext } from '@/backend/src/engine/Engine';
import { createDemoWorld } from '@/backend/src/engine/testHarness';
import type { ZoneDeviceInstance, Zone, Uuid } from '@/backend/src/domain/world';
import type { DeviceBlueprint } from '@/backend/src/domain/blueprints/deviceBlueprint';
import { deviceQuality } from '../../testUtils/deviceHelpers.ts';

function uuid(value: string): Uuid {
  return value as Uuid;
}

describe('Tick pipeline  CO coupling', () => {
  const QUALITY_POLICY = { sampleQuality01: (rng: () => number) => rng() } as const;
  const WORLD_SEED = 'co2-coupling';
  const CO2_TEST_BLUEPRINT: DeviceBlueprint = {
    id: '70000000-0000-0000-0000-000000000000',
    slug: 'test-co2-injector',
    class: 'device.test.co2',
    name: 'Test CO Injector',
    placementScope: 'zone',
    allowedRoomPurposes: ['growroom'],
    power_W: 80,
    efficiency01: 1,
    coverage_m2: 10,
    airflow_m3_per_h: 0
  } as DeviceBlueprint;

  function co2Device(id: string): ZoneDeviceInstance {
    const deviceId = uuid(id);
    return {
      id: deviceId,
      slug: CO2_TEST_BLUEPRINT.slug,
      name: CO2_TEST_BLUEPRINT.name,
      blueprintId: uuid(CO2_TEST_BLUEPRINT.id),
      placementScope: 'zone',
      quality01: deviceQuality(QUALITY_POLICY, WORLD_SEED, deviceId, CO2_TEST_BLUEPRINT),
      condition01: 1,
      powerDraw_W: CO2_TEST_BLUEPRINT.power_W,
      dutyCycle01: 1,
      efficiency01: CO2_TEST_BLUEPRINT.efficiency01,
      coverage_m2: CO2_TEST_BLUEPRINT.coverage_m2 ?? 0,
      airflow_m3_per_h: CO2_TEST_BLUEPRINT.airflow_m3_per_h ?? 0,
      sensibleHeatRemovalCapacity_W: 0,
      effects: ['co2'],
      effectConfigs: {
        co2: {
          target_ppm: 900,
          safetyMax_ppm: 1_000,
          pulse_ppm_per_tick: 200
        }
      }
    } satisfies ZoneDeviceInstance;
  }

  it('adds the injected ppm to the zone environment', () => {
    const world = createDemoWorld();
    const zone = world.company.structures[0].rooms[0].zones[0];
    zone.environment = { ...zone.environment, co2_ppm: 420 };
    zone.devices = [co2Device('70000000-0000-0000-0000-000000000001')];

    const { world: nextWorld } = runTick(world, { tickDurationHours: 1 });
    const nextZone = nextWorld.company.structures[0].rooms[0].zones[0];

    expect(nextZone.environment.co2_ppm).toBeCloseTo(620, 6);
  });

  it('ramps towards the target across multiple ticks without exceeding safety max', () => {
    let world = createDemoWorld();
    const zone = world.company.structures[0].rooms[0].zones[0];
    zone.environment = { ...zone.environment, co2_ppm: 420 };
    zone.devices = [co2Device('70000000-0000-0000-0000-000000000002')];

    const ctx: EngineRunContext = { tickDurationHours: HOURS_PER_TICK };

    for (let i = 0; i < 3; i += 1) {
      const tickResult = runTick(world, ctx);
      world = tickResult.world;
    }

    const nextZone = world.company.structures[0].rooms[0].zones[0];
    expect(nextZone.environment.co2_ppm).toBeCloseTo(900, 6);
    expect(nextZone.environment.co2_ppm).toBeLessThanOrEqual(1_000);
  });
});

import { describe, expect, it } from 'vitest';

import basicSoilPot from '../../../../../data/blueprints/cultivation-method/basic-soil-pot.json' with { type: 'json' };
import pot10L from '../../../../../data/blueprints/container/pot-10l.json' with { type: 'json' };
import soilMultiCycle from '../../../../../data/blueprints/substrate/soil-multi-cycle.json' with { type: 'json' };

import { PIPELINE_ORDER, type EngineRunContext } from '@/backend/src/engine/Engine';
import { createDemoWorld, runStages } from '@/backend/src/engine/testHarness';
import type {
  Plant,
  SimulationWorld,
  Structure,
  Room,
  Zone,
  WorkforceState,
  WorkforceTaskDefinition,
  WorkforceTaskInstance,
  Uuid,
} from '@/backend/src/domain/world';

function createTaskDefinition(taskCode: string, priority = 60): WorkforceTaskDefinition {
  return {
    taskCode,
    description: taskCode,
    requiredRoleSlug: 'cultivator',
    requiredSkills: [],
    priority,
    costModel: { basis: 'perPlant', laborMinutes: 10 },
  } satisfies WorkforceTaskDefinition;
}

function updateZone(
  world: SimulationWorld,
  updater: (structure: Structure, room: Room, zone: Zone) => Zone,
): SimulationWorld {
  const structure = world.company.structures[0];
  const room = structure.rooms[0];
  const zone = room.zones[0];
  const nextZone = updater(structure, room, zone);
  const nextRoom = { ...room, zones: [nextZone] } satisfies typeof room;
  const nextStructure = { ...structure, rooms: [nextRoom] } satisfies typeof structure;
  const nextCompany = { ...world.company, structures: [nextStructure] } satisfies typeof world.company;

  return {
    ...world,
    company: nextCompany,
  } satisfies SimulationWorld;
}

describe('cultivation method task scheduling integration', () => {
  it('queues deterministic maintenance tasks over successive harvest cycles', () => {
    let world = createDemoWorld();
    world = {
      ...world,
      simTimeHours: 24,
    } satisfies SimulationWorld;

    const workforceState: WorkforceState = {
      roles: [],
      employees: [],
      taskDefinitions: [
        createTaskDefinition('cultivation.repot', 80),
        createTaskDefinition(soilMultiCycle.reusePolicy.sterilizationTaskCode ?? 'cultivation.substrate.sterilize', 70),
        createTaskDefinition('cultivation.substrate.dispose', 90),
      ],
      taskQueue: [],
      kpis: [],
      warnings: [],
      payroll: {
        dayIndex: 0,
        totals: { baseMinutes: 0, otMinutes: 0, baseCost: 0, otCost: 0, totalLaborCost: 0 },
        byStructure: [],
      },
      market: { structures: [] },
    } satisfies WorkforceState;

    const basePlant: Omit<Plant, 'id'> = {
      name: 'Integration Plant',
      slug: 'integration-plant',
      strainId: '00000000-0000-4000-8000-000000000111' as Uuid,
      lifecycleStage: 'harvest-ready',
      ageHours: 640,
      health01: 0.92,
      biomass_g: 420,
      containerId: pot10L.id as Uuid,
      substrateId: soilMultiCycle.id as Uuid,
      readyForHarvest: false,
      status: 'harvested',
      harvestedAt_tick: 23,
    } satisfies Omit<Plant, 'id'>;

    world = updateZone(world, (_structure, _room, incomingZone) => ({
      ...incomingZone,
      cultivationMethodId: basicSoilPot.id as Uuid,
      containerId: pot10L.id as Uuid,
      substrateId: soilMultiCycle.id as Uuid,
      plants: [
        { ...basePlant, id: '00000000-0000-4000-8000-000000000201' as Uuid },
        { ...basePlant, id: '00000000-0000-4000-8000-000000000202' as Uuid },
      ],
    } satisfies Zone));

    world = {
      ...world,
      workforce: workforceState,
    } satisfies SimulationWorld;

    const ctx: EngineRunContext = { telemetry: { emit: () => {} } };
    let previousQueueLength = 0;

    function extractNewTasks(currentWorld: SimulationWorld): WorkforceTaskInstance[] {
      const queue = currentWorld.workforce?.taskQueue ?? [];
      const newEntries = queue.slice(previousQueueLength);
      previousQueueLength = queue.length;
      return newEntries;
    }

    // Cycle 1 - expect sterilisation task
    world = runStages(world, ctx, PIPELINE_ORDER);
    let newTasks = extractNewTasks(world);
    const steriliseCode = soilMultiCycle.reusePolicy.sterilizationTaskCode ?? 'cultivation.substrate.sterilize';
    expect(newTasks.map((task) => task.taskCode)).toEqual([steriliseCode]);

    // Prepare Cycle 2 - update harvest tick to current cycle
    world = updateZone(world, (_structure, _room, incomingZone) => {
      const updatedPlants = incomingZone.plants.map((plant) => ({
        ...plant,
        harvestedAt_tick: Math.trunc(world.simTimeHours) - 1,
      }));

      return { ...incomingZone, plants: updatedPlants } satisfies Zone;
    });

    world = runStages(world, ctx, PIPELINE_ORDER);
    newTasks = extractNewTasks(world);
    expect(newTasks.map((task) => task.taskCode)).toEqual(['cultivation.substrate.dispose']);

    // Prepare Cycle 3 - container service life reached, expect repot + sterilise
    world = updateZone(world, (_structure, _room, incomingZone) => {
      const updatedPlants = incomingZone.plants.map((plant) => ({
        ...plant,
        harvestedAt_tick: Math.trunc(world.simTimeHours) - 1,
      }));

      return { ...incomingZone, plants: updatedPlants } satisfies Zone;
    });

    world = runStages(world, ctx, PIPELINE_ORDER);
    newTasks = extractNewTasks(world);
    const sortedCodes = newTasks.map((task) => task.taskCode).sort();
    expect(sortedCodes).toEqual(['cultivation.repot', steriliseCode].sort());

    // Ensure contexts carry zone metadata for downstream scheduling
    newTasks.forEach((task) => {
      expect(task.context?.zoneId).toBe(world.company.structures[0]?.rooms[0]?.zones[0]?.id);
      expect(task.context?.structureId).toBe(world.company.structures[0]?.id);
    });
  });
});

import { describe, expect, it } from 'vitest';

import {
  CP_AIR_J_PER_KG_K,
  HOURS_PER_TICK,
  SECONDS_PER_HOUR
} from '@/backend/src/constants/simConstants';
import type { EngineRunContext } from '@/backend/src/engine/Engine';
import { runTick } from '@/backend/src/engine/Engine';
import { createDemoWorld } from '@/backend/src/engine/testHarness';
import { type DeviceQualityPolicy, type Uuid, type ZoneDeviceInstance } from '@/backend/src/domain/world';
import type { DeviceBlueprint } from '@/backend/src/domain/blueprints/deviceBlueprint';
import { deviceQuality } from '../../testUtils/deviceHelpers.ts';

function uuid(value: string): Uuid {
  return value as Uuid;
}

const QUALITY_POLICY: DeviceQualityPolicy = {
  sampleQuality01: (rng) => rng()
};

const WORLD_SEED = 'device-thermals-seed';

const LIGHTING_BLUEPRINT: DeviceBlueprint = {
  id: '20000000-0000-0000-0000-000000000002',
  slug: 'veg-light',
  class: 'device.test.lighting',
  name: 'Veg Light',
  placementScope: 'zone',
  allowedRoomPurposes: ['growroom'],
  power_W: 600,
  efficiency01: 0.2,
  coverage_m2: 60,
  airflow_m3_per_h: 0
};

const HVAC_BLUEPRINT: DeviceBlueprint = {
  id: '20000000-0000-0000-0000-000000000004',
  slug: 'zone-hvac',
  class: 'device.test.hvac',
  name: 'Zone HVAC',
  placementScope: 'zone',
  allowedRoomPurposes: ['growroom'],
  power_W: 800,
  efficiency01: 0.6,
  coverage_m2: 60,
  airflow_m3_per_h: 0
};

describe('Tick pipeline  device thermal effects', () => {
  it('integrates heat additions and HVAC removals across the first two stages', () => {
    const world = createDemoWorld();
    const structure = world.company.structures[0];
    const room = structure.rooms[0];
    const zone = room.zones[0];
    const initialTemperatureC = zone.environment.airTemperatureC;

    const lightingDeviceId = uuid('20000000-0000-0000-0000-000000000001');
    const lightingDevice: ZoneDeviceInstance = {
      id: lightingDeviceId,
      slug: LIGHTING_BLUEPRINT.slug,
      name: LIGHTING_BLUEPRINT.name,
      blueprintId: uuid(LIGHTING_BLUEPRINT.id),
      placementScope: 'zone',
      quality01: deviceQuality(QUALITY_POLICY, WORLD_SEED, lightingDeviceId, LIGHTING_BLUEPRINT),
      condition01: 0.94,
      powerDraw_W: 600,
      dutyCycle01: 1,
      efficiency01: 0.2,
      coverage_m2: 60,
      airflow_m3_per_h: 0,
      sensibleHeatRemovalCapacity_W: 0
    } satisfies ZoneDeviceInstance;

    const hvacDeviceId = uuid('20000000-0000-0000-0000-000000000003');
    const hvacDevice: ZoneDeviceInstance = {
      id: hvacDeviceId,
      slug: HVAC_BLUEPRINT.slug,
      name: HVAC_BLUEPRINT.name,
      blueprintId: uuid(HVAC_BLUEPRINT.id),
      placementScope: 'zone',
      quality01: deviceQuality(QUALITY_POLICY, WORLD_SEED, hvacDeviceId, HVAC_BLUEPRINT),
      condition01: 0.9,
      powerDraw_W: 800,
      dutyCycle01: 1,
      efficiency01: 0.6,
      coverage_m2: 60,
      airflow_m3_per_h: 0,
      sensibleHeatRemovalCapacity_W: 500
    } satisfies ZoneDeviceInstance;

    zone.devices = [lightingDevice, hvacDevice];

    const ctx: EngineRunContext = {
      tickDurationHours: 0.5
    } satisfies EngineRunContext;

    const { world: nextWorld } = runTick(world, ctx);

    const nextZone = nextWorld.company.structures[0].rooms[0].zones[0];
    const tickHours = (ctx as { tickDurationHours: number }).tickDurationHours ?? HOURS_PER_TICK;
    const tickSeconds = tickHours * SECONDS_PER_HOUR;
    const airMassKg = zone.airMass_kg;

    const heating_W = lightingDevice.powerDraw_W * (1 - lightingDevice.efficiency01);
    const hvacCooling_W = Math.min(
      hvacDevice.powerDraw_W * hvacDevice.dutyCycle01 * hvacDevice.efficiency01,
      hvacDevice.sensibleHeatRemovalCapacity_W
    );
    const additionDeltaC = (heating_W * tickSeconds) / (airMassKg * CP_AIR_J_PER_KG_K);
    const removalDeltaC = (hvacCooling_W * tickSeconds) / (airMassKg * CP_AIR_J_PER_KG_K);

    const expectedTemperatureC = initialTemperatureC + additionDeltaC - removalDeltaC;

    expect(nextZone.environment.airTemperatureC).toBeCloseTo(expectedTemperatureC, 10);
    expect((ctx as Record<string, unknown>).__wb_deviceEffects).toBeUndefined();
  });
});

import { describe, expect, it } from 'vitest';

import { runSeedToHarvest } from '@/backend/src/engine/seedToHarvest';
import type { Plant, SimulationWorld, Zone } from '@/backend/src/domain/world';
import { WHITE_WIDOW_STRAIN_ID } from '@/tests/testUtils/strainFixtures.ts';

type SeedToHarvestResult = ReturnType<typeof runSeedToHarvest>;
type StageTransition = SeedToHarvestResult['stageTransitions'][number];

function findZone(world: SimulationWorld, zoneId: Zone['id']): Zone | null {
  for (const structure of world.company.structures) {
    for (const room of structure.rooms) {
      const candidate = room.zones.find((zone) => zone.id === zoneId);

      if (candidate) {
        return candidate;
      }
    }
  }

  return null;
}

describe('seed-to-harvest orchestrator integration', () => {
  it('advances White Widow plants from seed to harvest-ready and stores deterministic lots', () => {
    const result = runSeedToHarvest({
      strainId: WHITE_WIDOW_STRAIN_ID
    });

    expect(result.ticksElapsed).toBe(2256);
    expect(result.stageTransitions).toHaveLength(18);

    const transitionsByPlant = new Map<Plant['id'], StageTransition[]>();

    for (const transition of result.stageTransitions) {
      const existing = transitionsByPlant.get(transition.plantId);

      if (existing) {
        existing.push(transition);
      } else {
        transitionsByPlant.set(transition.plantId, [transition]);
      }
    }

    for (const transitions of transitionsByPlant.values()) {
      const ordered = [...transitions].sort((left, right) => left.tick - right.tick);
      const ticks = ordered.map((event) => event.tick);

      expect(ordered).toHaveLength(3);
      expect(new Set(ordered.map((event) => event.plantId)).size).toBe(1);
      expect(ticks).toEqual([...ticks].sort((a, b) => a - b));
      expect(
        ordered.map((event) => `${event.from}->${event.to}`)
      ).toEqual(['seedling->vegetative', 'vegetative->flowering', 'flowering->harvest-ready']);
    }

    const photoperiodFlip = result.photoperiodTransitions[0];
    expect(result.photoperiodTransitions).toHaveLength(1);
    expect(photoperiodFlip).toMatchObject({
      tick: 823,
      fromPhase: 'vegetative',
      toPhase: 'flowering',
      previousSchedule: { onHours: 18, offHours: 6, startHour: 0 },
      nextSchedule: { onHours: 12, offHours: 12, startHour: 0 }
    });

    const zone = photoperiodFlip ? findZone(result.world, photoperiodFlip.zoneId) : null;
    expect(zone).not.toBeNull();
    expect(zone?.lightSchedule).toEqual(photoperiodFlip.nextSchedule);
    expect(zone?.photoperiodPhase).toBe('flowering');

    const finalPlants = zone?.plants ?? [];
    expect(finalPlants).toHaveLength(transitionsByPlant.size);
    expect(finalPlants.every((plant) => plant.lifecycleStage === 'harvest-ready')).toBe(true);
    expect(finalPlants.every((plant) => plant.status === 'harvested')).toBe(true);

    const plantIds = new Set([...transitionsByPlant.keys()]);
    const lotPlantIds = new Set(result.harvestedLots.map((lot) => lot.source.plantId));
    expect(lotPlantIds).toEqual(plantIds);

    expect(result.harvestedLots).toHaveLength(finalPlants.length);
    expect(result.totalBiomass_g).toBeCloseTo(6, 5);

    for (const lot of result.harvestedLots) {
      expect(lot.freshWeight_kg).toBeCloseTo(0.001, 6);
      expect(lot.moisture01).toBe(0.5);
      expect(lot.quality01).toBe(0);
      expect(lot.createdAt_tick).toBe(2255);
    }
  });
});

import { describe, expect, it } from 'vitest';

import { getSensorReadingsRuntime } from '@/backend/src/engine/pipeline/applySensors';
import { runTick, type EngineRunContext } from '@/backend/src/engine/Engine';
import { createDemoWorld } from '@/backend/src/engine/testHarness';
import type { SensorReading } from '@/backend/src/domain/interfaces/ISensor';
import type { ZoneDeviceInstance, Uuid } from '@/backend/src/domain/world';

function uuid(value: string): Uuid {
  return value as Uuid;
}

describe('Tick pipeline  sensor + actuator pattern', () => {
  it('Pattern D: Sensor reads pre-tick, actuator applies post-read', () => {
    const world = createDemoWorld();
    const zone = world.company.structures[0].rooms[0].zones[0];

    zone.environment = {
      ...zone.environment,
      airTemperatureC: 20,
    };

    const deviceId = uuid('60000000-0000-0000-0000-000000000001');
    const tempController: ZoneDeviceInstance = {
      id: deviceId,
      slug: 'temp-controller',
      name: 'Temperature Controller',
      blueprintId: uuid('60000000-0000-0000-0000-000000000002'),
      placementScope: 'zone',
      quality01: 1,
      condition01: 1,
      powerDraw_W: 500,
      dutyCycle01: 1,
      efficiency01: 0.8,
      coverage_m2: zone.floorArea_m2 ?? 0,
      airflow_m3_per_h: 0,
      sensibleHeatRemovalCapacity_W: 0,
      effects: ['sensor', 'thermal'],
      effectConfigs: {
        sensor: { measurementType: 'temperature', noise01: 0 },
        thermal: { mode: 'heat', max_heat_W: 500 },
      },
    } satisfies ZoneDeviceInstance;

    zone.devices = [tempController];

    const initialTemperature = zone.environment.airTemperatureC;

    const tickOneSnapshots: SensorReading<number>[] = [];
    const ctxTickOne: EngineRunContext = {
      instrumentation: {
        onStageComplete(stage) {
          if (stage !== 'applySensors') {
            return;
          }

          const runtime = getSensorReadingsRuntime(ctxTickOne);
          const readings = runtime?.deviceSensorReadings.get(deviceId) ?? [];
          tickOneSnapshots.push(...readings);
        },
      },
    } satisfies EngineRunContext;

    const { world: afterFirst } = runTick(world, ctxTickOne);
    const firstZone = afterFirst.company.structures[0].rooms[0].zones[0];

    expect(tickOneSnapshots).toHaveLength(1);
    expect(tickOneSnapshots[0]?.measuredValue).toBeCloseTo(initialTemperature, 5);
    expect(tickOneSnapshots[0]?.trueValue).toBeCloseTo(initialTemperature, 5);
    expect(tickOneSnapshots[0]?.noiseSample).toBe(0);
    expect(firstZone.environment.airTemperatureC).toBeGreaterThan(initialTemperature);

    const tickTwoSnapshots: SensorReading<number>[] = [];
    const baselineSecondTemp = firstZone.environment.airTemperatureC;
    const ctxTickTwo: EngineRunContext = {
      instrumentation: {
        onStageComplete(stage) {
          if (stage !== 'applySensors') {
            return;
          }

          const runtime = getSensorReadingsRuntime(ctxTickTwo);
          const readings = runtime?.deviceSensorReadings.get(deviceId) ?? [];
          tickTwoSnapshots.push(...readings);
        },
      },
    } satisfies EngineRunContext;

    const { world: afterSecond } = runTick(afterFirst, ctxTickTwo);
    const secondZone = afterSecond.company.structures[0].rooms[0].zones[0];

    expect(tickTwoSnapshots).toHaveLength(1);
    expect(tickTwoSnapshots[0]?.measuredValue).toBeCloseTo(baselineSecondTemp, 5);
    expect(tickTwoSnapshots[0]?.trueValue).toBeCloseTo(baselineSecondTemp, 5);
    expect(tickTwoSnapshots[0]?.noiseSample).toBe(0);
    expect(secondZone.environment.airTemperatureC).toBeGreaterThan(baselineSecondTemp);
  });
});

import { describe, expect, it } from 'vitest';

