import fs from 'node:fs/promises';
import { tmpdir } from 'node:os';
import path from 'node:path';
import { fileURLToPath } from 'node:url';

import { describe, expect, it, vi } from 'vitest';

import { hashCanonicalJson } from '@/shared/determinism/hash';
import {
  CURRENT_SAVE_SCHEMA_VERSION,
  createDefaultSaveGameMigrationRegistry,
  loadSaveGame
} from '@/backend/src/saveLoad/index';

const fixtureDir = fileURLToPath(new URL('../../fixtures/save/', import.meta.url));

function resolveFixture(...segments: string[]): string {
  return path.join(fixtureDir, ...segments);
}

async function writeTempSave(data: unknown): Promise<string> {
  const dir = await fs.mkdtemp(path.join(tmpdir(), 'wb-save-load-'));
  const filePath = path.join(dir, 'save.json');
  await fs.writeFile(filePath, `${JSON.stringify(data, null, 2)}\n`, 'utf8');
  return filePath;
}

describe('save/load integration', () => {
  it('loads the current schema fixture without migration', async () => {
    const filePath = resolveFixture('v1', 'basic.json');
    const save = await loadSaveGame(filePath);

    expect(save.schemaVersion).toBe(CURRENT_SAVE_SCHEMA_VERSION);

    const expected = JSON.parse(await fs.readFile(filePath, 'utf8')) as unknown;
    await expect(hashCanonicalJson(save)).resolves.toBe(await hashCanonicalJson(expected));
  });

  it('migrates a legacy save fixture to the current schema', async () => {
    const legacyPath = resolveFixture('v0', 'basic.json');
    const currentPath = resolveFixture('v1', 'basic.json');
    const registry = createDefaultSaveGameMigrationRegistry();

    const migrated = await loadSaveGame(legacyPath, { migrations: registry });
    const current = await loadSaveGame(currentPath, { migrations: registry });

    expect(migrated).toEqual(current);
    await expect(hashCanonicalJson(migrated)).resolves.toBe(await hashCanonicalJson(current));
  });

  it('rejects a save file with a corrupt schemaVersion header', async () => {
    const filePath = await writeTempSave({ schemaVersion: 'banana' });

    await expect(loadSaveGame(filePath)).rejects.toThrow(/schemaVersion/);
  });

  it('rejects saves missing company.structures', async () => {
    const baseline = JSON.parse(await fs.readFile(resolveFixture('v1', 'basic.json'), 'utf8')) as Record<
      string,
      unknown
    >;
    const world = baseline.world as Record<string, unknown>;
    const company = (world.company as Record<string, unknown>) ?? {};
    delete company.structures;
    world.company = company;

    const filePath = await writeTempSave(baseline);

    await expect(loadSaveGame(filePath)).rejects.toThrow(/company\.structures/);
  });

  it('does not invoke migrations when loading a current schema save', async () => {
    const filePath = resolveFixture('v1', 'basic.json');
    const registry = createDefaultSaveGameMigrationRegistry();
    const spy = vi.spyOn(registry, 'migrate');

    const save = await loadSaveGame(filePath, { migrations: registry });

    expect(save.schemaVersion).toBe(CURRENT_SAVE_SCHEMA_VERSION);
    expect(spy).not.toHaveBeenCalled();
  });
});

import { describe, expect, it } from "vitest";
import { readdir, readFile } from "node:fs/promises";
import path from "node:path";
import { fileURLToPath } from "node:url";

const testModuleDir = path.dirname(fileURLToPath(import.meta.url));
const engineSourceRoot = fileURLToPath(
  new URL("../../src/backend/src/", import.meta.url)
);
const enginePackageRoot = fileURLToPath(new URL("../../", import.meta.url));
const engineSrcRoot = fileURLToPath(new URL("../../src/", import.meta.url));
const engineTestsRoot = fileURLToPath(new URL("../", import.meta.url));

const pipelineDir = fileURLToPath(
  new URL("../../src/backend/src/engine/pipeline/", import.meta.url)
);
const domainSchemasDir = fileURLToPath(
  new URL("../../src/backend/src/domain/schemas/", import.meta.url)
);

const EXTRA_FILES = [
  path.join(engineSourceRoot, "domain", "schemas.ts")
];

const entrypoints = await collectEntrypoints();

describe("import resolution guardrail", () => {
  for (const entrypoint of entrypoints) {
    it(`dynamically imports ${entrypoint.label}`, async () => {
      await expect(import(entrypoint.specifier)).resolves.toBeDefined();
    });
  }
});

describe("JSON import attributes", () => {
  it("requires Node.js 22 import attributes on JSON imports", async () => {
    const roots = [engineSrcRoot, engineTestsRoot];
    const jsonImportPattern = /import[\s\S]*?from\s+['"][^'"]+\.json['"][\s\S]*?;/g;
    const attributePattern = /with\s*\{\s*type\s*:\s*['"]json['"]\s*\}/;

    const violations: string[] = [];

    for (const root of roots) {
      const files = await collectTsFilesRecursive(root);

      for (const filePath of files) {
        const source = await readFile(filePath, "utf8");

        for (const match of source.matchAll(jsonImportPattern)) {
          if (!attributePattern.test(match[0])) {
            const relativePath = path.relative(enginePackageRoot, filePath);
            const snippet = match[0]
              .replace(/\s+/g, " ")
              .trim()
              .slice(0, 160);
            violations.push(`${relativePath}: ${snippet}`);
          }
        }
      }
    }

    expect(violations).toEqual([]);
  });
});

async function collectEntrypoints() {
  const files = [
    ...(await listImmediateTsFiles(pipelineDir)),
    ...(await listImmediateTsFiles(domainSchemasDir)),
    ...EXTRA_FILES
  ];

  const sorted = files
    .filter((file) => file.endsWith(".ts") && !file.endsWith(".d.ts"))
    .sort((a, b) => a.localeCompare(b));

  return sorted.map((filePath) => ({
    filePath,
    label: path.relative(engineSourceRoot, filePath),
    specifier: toImportSpecifier(filePath)
  }));
}

async function listImmediateTsFiles(directory) {
  const entries = await readdir(directory, { withFileTypes: true });
  return entries
    .filter((entry) => entry.isFile() && entry.name.endsWith(".ts"))
    .map((entry) => path.join(directory, entry.name));
}

function toImportSpecifier(absPath) {
  const relativePath = path.relative(testModuleDir, absPath);
  const normalized = relativePath.split(path.sep).join("/");
  return normalized.startsWith(".") ? normalized : `./${normalized}`;
}

async function collectTsFilesRecursive(directory) {
  const entries = await readdir(directory, { withFileTypes: true });
  const files: string[] = [];

  for (const entry of entries) {
    if (entry.name === "node_modules" || entry.name === "dist") {
      continue;
    }

    const fullPath = path.join(directory, entry.name);

    if (entry.isDirectory()) {
      files.push(...(await collectTsFilesRecursive(fullPath)));
      continue;
    }

    if (entry.isFile() && isTypeScriptSource(entry.name)) {
      files.push(fullPath);
    }
  }

  return files.sort((a, b) => a.localeCompare(b));
}

function isTypeScriptSource(filename: string): boolean {
  return /\.(?:[cm]?ts|tsx)$/.test(filename) && !filename.endsWith(".d.ts");
}

