    } satisfies SimulationWorld;

    const ctx: EngineContext = {};
    const runtime = ensureCultivationTaskRuntime(ctx);
    const catalog = getCultivationMethodCatalog();
    const currentTick = Math.trunc(worldWithWorkforce.simTimeHours);

    const firstPassTasks = scheduleCultivationTasksForZone({
      world: worldWithWorkforce,
      structure,
      room,
      zone,
      workforce: workforceState,
      runtime,
      currentTick,
      methodCatalog: catalog,
    });

    const steriliseCode = soilMultiCycle.reusePolicy.sterilizationTaskCode ?? 'cultivation.substrate.sterilize';
    expect(firstPassTasks, 'first harvest schedules sterilisation').toHaveLength(1);
    expect(firstPassTasks[0]?.taskCode).toBe(steriliseCode);

    // Second cycle should trigger substrate disposal (maxCycles = 2)
    const secondCyclePlants: Plant[] = zone.plants.map((plant) => ({
      ...plant,
      harvestedAt_tick: currentTick,
    }));
    const secondCycleZone: Zone = { ...zone, plants: secondCyclePlants } satisfies Zone;
    const secondCycleWorld = {
      ...worldWithWorkforce,
      simTimeHours: worldWithWorkforce.simTimeHours + 1,
    } satisfies SimulationWorld;

    const secondPassTasks = scheduleCultivationTasksForZone({
      world: secondCycleWorld,
      structure,
      room,
      zone: secondCycleZone,
      workforce: workforceState,
      runtime,
      currentTick: Math.trunc(secondCycleWorld.simTimeHours),
      methodCatalog: catalog,
    });

    expect(secondPassTasks, 'second harvest schedules disposal').toHaveLength(1);
    expect(secondPassTasks[0]?.taskCode).toBe('cultivation.substrate.dispose');

    // Third cycle should trigger container repot (service life 3) and another sterilisation.
    const thirdCyclePlants: Plant[] = zone.plants.map((plant) => ({
      ...plant,
      harvestedAt_tick: Math.trunc(secondCycleWorld.simTimeHours),
    }));
    const thirdCycleZone: Zone = { ...zone, plants: thirdCyclePlants } satisfies Zone;
    const thirdCycleWorld = {
      ...secondCycleWorld,
      simTimeHours: secondCycleWorld.simTimeHours + 1,
    } satisfies SimulationWorld;

    const thirdPassTasks = scheduleCultivationTasksForZone({
      world: thirdCycleWorld,
      structure,
      room,
      zone: thirdCycleZone,
      workforce: workforceState,
      runtime,
      currentTick: Math.trunc(thirdCycleWorld.simTimeHours),
      methodCatalog: catalog,
    });

    const taskCodes = thirdPassTasks.map((task) => task.taskCode).sort();
    expect(taskCodes).toEqual(['cultivation.repot', steriliseCode].sort());

    const contexts = thirdPassTasks.map((task) => task.context ?? {});
    contexts.forEach((context) => {
      expect(context.zoneId).toBe(zone.id);
      expect(context.structureId).toBe(structure.id);
    });
  });
});

import { readdirSync, readFileSync } from 'node:fs';
import path from 'node:path';

import { describe, expect, it } from 'vitest';

import {
  parseContainerBlueprint,
  parseCultivationMethodBlueprint,
  parseDeviceBlueprint,
  parseDiseaseBlueprint,
  parseIrrigationBlueprint,
  parsePestBlueprint,
  parsePersonnelRoleBlueprint,
  parsePersonnelSkillBlueprint,
  parseRoomPurposeBlueprint,
  parseStructureBlueprint,
  parseStrainBlueprint,
  parseSubstrateBlueprint
} from '@/backend/src/domain/blueprints/index';
import { resolveBlueprintPath } from '../../testUtils/paths.ts';
import { fmtNum } from '@/backend/src/util/format.ts';

type ParserFn = (input: unknown, context: { readonly filePath: string; readonly relativePath: string }) => void;

const blueprintRoot = resolveBlueprintPath('');

function listBlueprintFiles(relativeDir = ''): string[] {
  const absolute = relativeDir ? path.join(blueprintRoot, relativeDir) : blueprintRoot;
  const entries = readdirSync(absolute, { withFileTypes: true });
  const files: string[] = [];

  for (const entry of entries) {
    const nextRelative = relativeDir ? `${relativeDir}/${entry.name}` : entry.name;

    if (entry.isDirectory()) {
      files.push(...listBlueprintFiles(nextRelative));
    } else if (entry.isFile() && entry.name.endsWith('.json')) {
      files.push(nextRelative);
    }
  }

  return files.sort();
}

function readBlueprint(relativePath: string): unknown {
  const filePath = resolveBlueprintPath(relativePath);
  const raw = readFileSync(filePath, 'utf8');
  return JSON.parse(raw) as unknown;
}

const substrateSlugRegistry = (() => {
  const slugs = new Set<string>();
  const substrateFiles = listBlueprintFiles('substrate');

  for (const relative of substrateFiles) {
    const filePath = resolveBlueprintPath(relative);
    const parsed = parseSubstrateBlueprint(readBlueprint(relative), { filePath });
    slugs.add(parsed.slug);
  }

  return slugs;
})();

function resolveParser(relativePath: string): { readonly label: string; readonly parse: ParserFn } {
  const [domain, namespace] = relativePath.split('/');
  const filePath = resolveBlueprintPath(relativePath);

  switch (domain) {
    case 'container':
      return {
        label: 'container',
        parse: (input, _context) => {
          parseContainerBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'cultivation-method':
      return {
        label: 'cultivation-method',
        parse: (input, _context) => {
          parseCultivationMethodBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'structure':
      return {
        label: 'structure',
        parse: (input, _context) => {
          parseStructureBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'room':
      if (namespace === 'purpose') {
        return {
          label: 'room.purpose',
          parse: (input, _context) => {
            parseRoomPurposeBlueprint(input, { filePath });
          }
        } satisfies { label: string; parse: ParserFn };
      }
      break;
    case 'disease':
      return {
        label: 'disease',
        parse: (input, _context) => {
          parseDiseaseBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'pest':
      return {
        label: 'pest',
        parse: (input, _context) => {
          parsePestBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'personnel':
      if (namespace === 'role') {
        return {
          label: 'personnel.role',
          parse: (input, _context) => {
            parsePersonnelRoleBlueprint(input, { filePath });
          }
        } satisfies { label: string; parse: ParserFn };
      }
      if (namespace === 'skill') {
        return {
          label: 'personnel.skill',
          parse: (input, _context) => {
            parsePersonnelSkillBlueprint(input, { filePath });
          }
        } satisfies { label: string; parse: ParserFn };
      }
      break;
    case 'device':
      return {
        label: 'device',
        parse: (input, _context) => {
          parseDeviceBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'irrigation':
      return {
        label: 'irrigation',
        parse: (input, _context) => {
          parseIrrigationBlueprint(input, { filePath, knownSubstrateSlugs: substrateSlugRegistry });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'strain':
      return {
        label: 'strain',
        parse: (input, _context) => {
          parseStrainBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    case 'substrate':
      return {
        label: 'substrate',
        parse: (input, _context) => {
          parseSubstrateBlueprint(input, { filePath });
        }
      } satisfies { label: string; parse: ParserFn };
    default:
      throw new Error(`No parser registered for blueprint path "${relativePath}".`);
  }

  throw new Error(`Unsupported personnel blueprint namespace in path "${relativePath}".`);
}

interface StrictCase {
  readonly label: string;
  readonly samplePath: string;
  readonly parse: ParserFn;
}

const strictBlueprintCases: readonly StrictCase[] = [
  {
    label: 'container',
    samplePath: 'container/pot-10l.json',
    parse: (input, ctx) => parseContainerBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'cultivation-method',
    samplePath: 'cultivation-method/basic-soil-pot.json',
    parse: (input, ctx) => parseCultivationMethodBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'structure',
    samplePath: 'structure/shed.json',
    parse: (input, ctx) => parseStructureBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'room.purpose',
    samplePath: 'room/purpose/growroom.json',
    parse: (input, ctx) => parseRoomPurposeBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'disease',
    samplePath: 'disease/botrytis-gray-mold-bud-rot.json',
    parse: (input, ctx) => parseDiseaseBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'pest',
    samplePath: 'pest/aphids.json',
    parse: (input, ctx) => parsePestBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'personnel.role',
    samplePath: 'personnel/role/gardener.json',
    parse: (input, ctx) => parsePersonnelRoleBlueprint(input, { filePath: ctx.filePath })
  },
  {
    label: 'personnel.skill',
    samplePath: 'personnel/skill/gardening.json',
    parse: (input, ctx) => parsePersonnelSkillBlueprint(input, { filePath: ctx.filePath })
  }
];

describe('blueprint schema coverage', () => {
  it('parses every blueprint using the canonical schemas', () => {
    const relativePaths = listBlueprintFiles();
    const summary = new Map<string, number>();

    for (const relative of relativePaths) {
      const parser = resolveParser(relative);
      expect(() => { parser.parse(readBlueprint(relative), { filePath: resolveBlueprintPath(relative), relativePath: relative }); })
        .not.toThrow();

      summary.set(parser.label, (summary.get(parser.label) ?? 0) + 1);
    }

    const summaryLine = Array.from(summary.entries())
      .map(([label, count]) => `${label}=${fmtNum(count)}`)
      .join(', ');

    console.info(`[Blueprint coverage] ${summaryLine}`);
  });

  describe.each(strictBlueprintCases)('%s strict schema guards', (testCase) => {
    const filePath = resolveBlueprintPath(testCase.samplePath);

    it('rejects missing required fields', () => {
      const blueprint = readBlueprint(testCase.samplePath) as Record<string, unknown>;
      const invalid = { ...blueprint };
      delete invalid.id;

      expect(() => { testCase.parse(invalid, { filePath, relativePath: testCase.samplePath }); }).toThrow();
    });

    it('rejects unexpected top-level properties', () => {
      const blueprint = readBlueprint(testCase.samplePath) as Record<string, unknown>;
      const invalid = { ...blueprint, __unexpected: true };

      expect(() => { testCase.parse(invalid, { filePath, relativePath: testCase.samplePath }); }).toThrow();
    });
  });
});

import path from 'node:path';
import { fileURLToPath } from 'node:url';

import { describe, expect, it } from 'vitest';

import dripInline from '../../../../../data/blueprints/irrigation/drip-inline-fertigation-basic.json' with { type: 'json' };
import ebbFlow from '../../../../../data/blueprints/irrigation/ebb-flow-table-small.json' with { type: 'json' };
import manualCan from '../../../../../data/blueprints/irrigation/manual-watering-can.json' with { type: 'json' };
import topFeed from '../../../../../data/blueprints/irrigation/top-feed-pump-timer.json' with { type: 'json' };
import cocoCoir from '../../../../../data/blueprints/substrate/coco-coir.json' with { type: 'json' };
import soilMulti from '../../../../../data/blueprints/substrate/soil-multi-cycle.json' with { type: 'json' };
import soilSingle from '../../../../../data/blueprints/substrate/soil-single-cycle.json' with { type: 'json' };
import basicSoilPot from '../../../../../data/blueprints/cultivation-method/basic-soil-pot.json' with { type: 'json' };
import scrog from '../../../../../data/blueprints/cultivation-method/screen-of-green.json' with { type: 'json' };
import sog from '../../../../../data/blueprints/cultivation-method/sea-of-green.json' with { type: 'json' };

import { parseIrrigationBlueprint, type IrrigationBlueprint } from '@/backend/src/domain/world';

type CultivationBlueprint = typeof basicSoilPot;

describe('irrigation compatibility coverage', () => {
  const substrateSlugs = new Set([
    cocoCoir.slug,
    soilMulti.slug,
    soilSingle.slug
  ]);

const irrigationFixtures = [
  {
    data: dripInline,
    path: fileURLToPath(
      new URL('../../../../../data/blueprints/irrigation/drip-inline-fertigation-basic.json', import.meta.url)
    )
  },
  {
    data: ebbFlow,
    path: fileURLToPath(
      new URL('../../../../../data/blueprints/irrigation/ebb-flow-table-small.json', import.meta.url)
    )
  },
  {
    data: manualCan,
    path: fileURLToPath(
      new URL('../../../../../data/blueprints/irrigation/manual-watering-can.json', import.meta.url)
    )
  },
  {
    data: topFeed,
    path: fileURLToPath(
      new URL('../../../../../data/blueprints/irrigation/top-feed-pump-timer.json', import.meta.url)
    )
  }
] as const;
const blueprintsRoot = path.resolve(
  fileURLToPath(new URL('../../../../../data/blueprints/', import.meta.url))
);
const irrigationBlueprints = irrigationFixtures.map((fixture) =>
  parseIrrigationBlueprint(fixture.data, {
    knownSubstrateSlugs: substrateSlugs,
    filePath: fixture.path,
    blueprintsRoot
  })
);

  const cultivationMethods = [basicSoilPot, scrog, sog] as const satisfies readonly CultivationBlueprint[];

  const compatibilityIndex = new Map<string, IrrigationBlueprint[]>();
  irrigationBlueprints.forEach((blueprint) => {
    blueprint.compatibility.substrates.forEach((substrateSlug) => {
      const existing = compatibilityIndex.get(substrateSlug) ?? [];
      existing.push(blueprint);
      compatibilityIndex.set(substrateSlug, existing);
    });
  });

  function resolveCompatibleIrrigation(substrateSlug: string): IrrigationBlueprint | undefined {
    const compatible = compatibilityIndex.get(substrateSlug);
    return compatible?.[0];
  }

  it('covers every cultivation substrate with at least one irrigation method', () => {
    cultivationMethods.forEach((method) => {
      method.substrates.forEach((substrateSlug: string) => {
        const compatibleMethods = compatibilityIndex.get(substrateSlug);
        expect(compatibleMethods, `No irrigation method exposes substrate ${substrateSlug}`).toBeDefined();
        expect(compatibleMethods?.length ?? 0).toBeGreaterThan(0);
      });
    });
  });

  it('resolves default cultivation substrates to an irrigation method automatically', () => {
    cultivationMethods.forEach((method) => {
      const defaultSubstrate = method.meta?.defaults?.substrateSlug as string | undefined;
      expect(defaultSubstrate, `Cultivation method ${method.slug} is missing a default substrate`).toBeTruthy();

      if (!defaultSubstrate) {
        return;
      }

      const resolved = resolveCompatibleIrrigation(defaultSubstrate);
      expect(resolved, `No irrigation method compatible with ${defaultSubstrate}`).toBeTruthy();
    });
  });
});

import fs from 'node:fs';
import path from 'node:path';

import { beforeEach, describe, expect, it, vi } from 'vitest';

import {
  clearStrainBlueprintCache,
  loadAllStrainBlueprints,
  loadStrainBlueprint
} from '@/backend/src/domain/blueprints/strainBlueprintLoader';
import type { StrainBlueprint } from '@/backend/src/domain/blueprints/strainBlueprint';
import type { Uuid } from '@/backend/src/domain/entities';
import { resolveBlueprintPath } from '../../testUtils/paths.ts';
import {
  AK47_STRAIN_ID,
  WHITE_WIDOW_STRAIN_ID
} from '../../testUtils/strainFixtures.ts';
import { expectDefined } from '../../util/expectors';

const blueprintsRoot = path.resolve(resolveBlueprintPath(''));

describe('strainBlueprintLoader', () => {
  beforeEach(() => {
    clearStrainBlueprintCache();
    vi.restoreAllMocks();
  });

  it('loadAllStrainBlueprints loads all strain blueprints', () => {
    const blueprints = loadAllStrainBlueprints({ blueprintsRoot });

    expect(blueprints.size).toBeGreaterThanOrEqual(5);
    const whiteWidow = expectDefined(blueprints.get(WHITE_WIDOW_STRAIN_ID));
    expect(whiteWidow.slug).toBe('white-widow');
    expect(whiteWidow.class).toBe('strain');
  });

  it('loadStrainBlueprint retrieves a specific blueprint', () => {
    const blueprint = loadStrainBlueprint(WHITE_WIDOW_STRAIN_ID, { blueprintsRoot });

    expect(blueprint).not.toBeNull();
    expect(blueprint?.name).toBe('White Widow');
    expect(blueprint?.slug).toBe('white-widow');
    expect(blueprint?.growthModel).toBeDefined();
    expect(blueprint?.envBands).toBeDefined();
  });

  it('loadStrainBlueprint returns null for unknown id', () => {
    const unknownId = '99999999-9999-9999-9999-999999999999' as Uuid;
    const blueprint = loadStrainBlueprint(unknownId, { blueprintsRoot });

    expect(blueprint).toBeNull();
  });

  it('shares cache between loadAllStrainBlueprints calls', () => {
    const readSpy = vi.spyOn(fs, 'readFileSync');

    const first = loadAllStrainBlueprints({ blueprintsRoot });
    const initialReads = readSpy.mock.calls.length;
    const second = loadAllStrainBlueprints({ blueprintsRoot });

    expect(first.size).toBeGreaterThanOrEqual(5);
    expect(second.size).toBe(first.size);
    expect(readSpy.mock.calls.length).toBe(initialReads);
  });

  it('clears cache via clearStrainBlueprintCache', () => {
    loadAllStrainBlueprints({ blueprintsRoot });
    clearStrainBlueprintCache();

    const readSpy = vi.spyOn(fs, 'readFileSync');
    loadAllStrainBlueprints({ blueprintsRoot });

    expect(readSpy).toHaveBeenCalled();
  });

  it('throws when blueprints root is invalid', () => {
    expect(() => loadAllStrainBlueprints({ blueprintsRoot: '/invalid/path' })).toThrow();
  });

  it('loadStrainBlueprint caches individual lookups', () => {
    const readSpy = vi.spyOn(fs, 'readFileSync');

    const first = expectDefined(loadStrainBlueprint(AK47_STRAIN_ID, { blueprintsRoot }));
    const afterFirstReads = readSpy.mock.calls.length;

    const second = expectDefined(loadStrainBlueprint(AK47_STRAIN_ID, { blueprintsRoot }));
    expect(second).toBe(first);
    expect(readSpy.mock.calls.length).toBe(afterFirstReads);
  });
});

import { readFileSync } from 'node:fs';
import path from 'node:path';

import { describe, expect, it } from 'vitest';

import { parseStrainBlueprint } from '../../../src/backend/src/domain/blueprints/strainBlueprint.ts';
import { BlueprintTaxonomyMismatchError } from '../../../src/backend/src/domain/blueprints/taxonomy.ts';
import { resolveBlueprintPath } from '../../testUtils/paths.ts';

const fixturePath = resolveBlueprintPath('strain/white-widow.json');
const blueprintsRoot = path.resolve(fixturePath, '..', '..');
const fixturePayload = JSON.parse(readFileSync(fixturePath, 'utf8'));

describe('strainBlueprintSchema', () => {
  it('parses the white widow blueprint', () => {
    const blueprint = parseStrainBlueprint(fixturePayload, {
      filePath: fixturePath,
      blueprintsRoot
    });
    expect(blueprint.id).toBeDefined();
    expect(blueprint.slug).toBe('white-widow');
    expect(blueprint.envBands.default.temp_C?.green).toEqual([21, 26]);
  });

  it('rejects payloads with missing required fields', () => {
    expect(() => parseStrainBlueprint({})).toThrow();
    expect(() => parseStrainBlueprint({ ...fixturePayload, id: undefined })).toThrow();
    expect(() => parseStrainBlueprint({ ...fixturePayload, slug: 'Invalid Slug' })).toThrow();
  });

  it('rejects invalid class formats', () => {
    expect(() =>
      parseStrainBlueprint(
        { ...fixturePayload, class: 'strain.hybrid' },
        { filePath: fixturePath }
      )
    ).toThrow();
    expect(() =>
      parseStrainBlueprint(
        { ...fixturePayload, class: 'device.lighting' },
        { filePath: fixturePath }
      )
    ).toThrow();
  });

  it('validates env bands structure', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        envBands: { default: { temp_C: { green: [25, 20], yellowLow: 18, yellowHigh: 28 } } }
      })
    ).toThrow();
  });

  it('validates stress tolerance positivity', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        stressTolerance: { ...fixturePayload.stressTolerance, temp_C: -1 }
      })
    ).toThrow();
  });

  it('validates growth model structure', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        growthModel: {
          ...fixturePayload.growthModel,
          temperature: { ...fixturePayload.growthModel.temperature, min_C: 40 }
        }
      })
    ).toThrow();
  });

  it('accepts numeric dryMatterFraction and harvestIndex', () => {
    const payload = {
      ...fixturePayload,
      growthModel: {
        ...fixturePayload.growthModel,
        dryMatterFraction: 0.3,
        harvestIndex: 0.65
      }
    };

    expect(() =>
      parseStrainBlueprint(payload, {
        filePath: fixturePath,
        blueprintsRoot
      })
    ).not.toThrow();
  });

  it('accepts stage-specific dryMatterFraction and harvestIndex objects', () => {
    expect(() =>
      parseStrainBlueprint(
        {
          ...fixturePayload,
          growthModel: {
            ...fixturePayload.growthModel,
            dryMatterFraction: { vegetation: 0.25, flowering: 0.2 },
            harvestIndex: { targetFlowering: 0.7 }
          }
        },
        { filePath: fixturePath, blueprintsRoot }
      )
    ).not.toThrow();
  });

  it('rejects stage fractions outside of [0, 1]', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        growthModel: {
          ...fixturePayload.growthModel,
          dryMatterFraction: { vegetation: 1.2 }
        }
      })
    ).toThrow();

    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        growthModel: {
          ...fixturePayload.growthModel,
          harvestIndex: { targetFlowering: -0.1 }
        }
      })
    ).toThrow();
  });

  it('validates phase durations are positive integers', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        phaseDurations: { ...fixturePayload.phaseDurations, seedlingDays: -1 }
      })
    ).toThrow();
  });

  it('validates noise configuration bounds', () => {
    expect(() =>
      parseStrainBlueprint({
        ...fixturePayload,
        noise: { enabled: true, pct: 1.5 }
      })
    ).toThrow();
  });

  it('validates taxonomy when filePath provided', () => {
    expect(() =>
      parseStrainBlueprint(
        { ...fixturePayload, class: 'strain' },
        { filePath: fixturePath, blueprintsRoot }
      )
    ).not.toThrow();
  });

  it('throws BlueprintTaxonomyMismatchError when path disagrees', () => {
    expect(() =>
      parseStrainBlueprint(fixturePayload, {
        filePath: path.join(blueprintsRoot, 'device/climate/cool-air-split-3000.json'),
        blueprintsRoot
      })
    ).toThrow(BlueprintTaxonomyMismatchError);
  });

  it('detects duplicate slugs in registry', () => {
    const registry = new Map<string, string>();
    parseStrainBlueprint(fixturePayload, {
      slugRegistry: registry,
      filePath: fixturePath,
      blueprintsRoot
    });

    expect(() =>
      parseStrainBlueprint(fixturePayload, {
        slugRegistry: registry,
        filePath: fixturePath,
        blueprintsRoot
      })
    ).toThrow();
  });

  it('accepts unique slugs across multiple parses', () => {
    const registry = new Map<string, string>();
    parseStrainBlueprint({ ...fixturePayload, slug: 'strain-a' }, { slugRegistry: registry, blueprintsRoot });
    expect(() =>
      parseStrainBlueprint({ ...fixturePayload, slug: 'strain-b' }, { slugRegistry: registry, blueprintsRoot })
    ).not.toThrow();
  });
});

/* eslint-disable wb-sim/no-economy-per-tick */
import { parser as typescriptEslintParser } from 'typescript-eslint';
import { Linter } from 'eslint';
import { describe, expect, it } from 'vitest';

import { noEconomyPerTickRule } from '../../../../../tools/eslint/rules/no-economy-per-tick';

const linter = new Linter({ configType: 'eslintrc' });
linter.defineParser(
  '@typescript-eslint/parser',
  typescriptEslintParser as unknown as Linter.ParserModule
);
linter.defineRule('wb-sim/no-economy-per-tick', noEconomyPerTickRule);

function lint(code: string) {
  const messages = linter.verify(
    code,
    {
      parser: '@typescript-eslint/parser',
      parserOptions: {
        ecmaVersion: 2022,
        sourceType: 'module'
      },
      rules: {
        'wb-sim/no-economy-per-tick': 'error'
      }
    },
    { filename: 'packages/engine/src/economy/example.ts' }
  );

  return messages.map((message) => ({ ruleId: message.ruleId, message: message.message }));
}

describe('no-economy-per-tick ESLint rule', () => {
