import { describe, expect, it } from 'vitest';

import { resolveStorageRoomForStructure } from '@/backend/src/services/storage/resolveStorageRoom';
import { createDemoWorld } from '@/backend/src/engine/testHarness';
import type { Room } from '@/backend/src/domain/world';

type Mutable<T> = { -readonly [K in keyof T]: T[K] };

describe('resolveStorageRoomForStructure', () => {
  it('resolves a storage room by class', () => {
    const world = createDemoWorld();
    const structure = world.company.structures[0];

    const result = resolveStorageRoomForStructure(structure.id, world);

    expect(result.ok).toBe(true);
    if (!result.ok) {
      return;
    }

    expect(result.room.class).toBe('room.storage');
  });

  it('resolves a storage room by storage tag when class is missing', () => {
    const world = createDemoWorld();
    const structure = world.company.structures[0];
    const storageRoom = structure.rooms.find((room) => room.class === 'room.storage');

    if (!storageRoom) {
      throw new Error('Expected demo world to include a storage room');
    }

    const mutableRoom = storageRoom as Mutable<Room>;
    mutableRoom.class = undefined;
    mutableRoom.tags = ['storage'];

    const result = resolveStorageRoomForStructure(structure.id, world);

    expect(result.ok).toBe(true);
    if (!result.ok) {
      return;
    }

    expect(result.room.tags).toContain('storage');
  });

  it('returns not_found when no storage room is available', () => {
    const world = createDemoWorld();
    const structure = world.company.structures[0];
    const storageRoom = structure.rooms.find((room) => room.class === 'room.storage');

    if (!storageRoom) {
      throw new Error('Expected demo world to include a storage room');
    }

    const mutableRoom = storageRoom as Mutable<Room>;
    mutableRoom.class = undefined;
    mutableRoom.tags = [];

    const result = resolveStorageRoomForStructure(structure.id, world);

    expect(result).toEqual({ ok: false, reason: 'not_found', candidates: [] });
  });

  it('returns ambiguous when multiple storage rooms are present', () => {
    const world = createDemoWorld();
    const structure = world.company.structures[0];
    const storageRoom = structure.rooms.find((room) => room.class === 'room.storage');

    if (!storageRoom) {
      throw new Error('Expected demo world to include a storage room');
    }

    const mutableStructure = structure as Mutable<typeof structure>;
    mutableStructure.rooms = [
      ...structure.rooms,
      {
        ...storageRoom,
        id: '00000000-0000-0000-0000-00000000ffff' as Room['id']
      }
    ];

    const result = resolveStorageRoomForStructure(structure.id, world);

    expect(result).toEqual({
      ok: false,
      reason: 'ambiguous',
      candidates: expect.arrayContaining([storageRoom.id, '00000000-0000-0000-0000-00000000ffff'])
    });
  });
});

import { afterEach, describe, expect, it, vi } from 'vitest';

import type { EmployeeRngSeedUuid } from '@/backend/src/domain/workforce/Employee';
import { getTraitMetadata } from '@/backend/src/domain/workforce/traits';
import { resolveWorkforceIdentity } from '@/backend/src/services/workforce/identitySource';

describe('resolveWorkforceIdentity', () => {
  const ONLINE_SEED = 'online-seed';
  const RNG_SEED_ALPHA = '018f29ce-0000-7000-8000-0000000000a1' as EmployeeRngSeedUuid;
  const RNG_SEED_BRAVO = '018f29ce-0000-7000-8000-0000000000b2' as EmployeeRngSeedUuid;
  const RNG_SEED_CHARLIE = '018f29ce-0000-7000-8000-0000000000c3' as EmployeeRngSeedUuid;

  afterEach(() => {
    vi.useRealTimers();
    vi.restoreAllMocks();
    vi.unstubAllGlobals();
  });

  it('returns identity from randomuser when the HTTP request succeeds', async () => {
    const fetchMock = vi.fn<Parameters<typeof fetch>, ReturnType<typeof fetch>>(() =>
      Promise.resolve({
        ok: true,
        json: async () => ({
          results: [
            {
              gender: 'male',
              name: { first: 'Jordan', last: 'Rivera' },
            },
          ],
        }),
      } as unknown as Response),
    );

    vi.stubGlobal('fetch', fetchMock as unknown as typeof fetch);

    const identity = await resolveWorkforceIdentity({
      randomUserSeed: ONLINE_SEED,
      rngSeedUuid: RNG_SEED_ALPHA,
    });

    expect(fetchMock).toHaveBeenCalledTimes(1);
    const requestUrl = fetchMock.mock.calls[0][0] as URL;
    expect(requestUrl).toBeInstanceOf(URL);
    expect(requestUrl.searchParams.get('seed')).toBe(ONLINE_SEED);
    expect(requestUrl.searchParams.get('inc')).toBe('gender,name');
    expect(requestUrl.searchParams.get('noinfo')).toBe('true');

    expect(identity).toMatchObject({
      firstName: 'Jordan',
      lastName: 'Rivera',
      gender: 'm',
      source: 'randomuser',
    });

    expect(identity.traits.length).toBeGreaterThanOrEqual(1);
    expect(identity.traits.length).toBeLessThanOrEqual(2);
    for (const trait of identity.traits) {
      expect(getTraitMetadata(trait.id)).toBeDefined();
    }
  });

  it('falls back to pseudodata when the randomuser request times out', async () => {
    const fetchMock = vi.fn<Parameters<typeof fetch>, ReturnType<typeof fetch>>((_, init) =>
      new Promise((_, reject) => {
        init?.signal?.addEventListener('abort', () => {
          reject(new DOMException('aborted', 'AbortError'));
        });
      }),
    );

    vi.stubGlobal('fetch', fetchMock as unknown as typeof fetch);
    vi.useFakeTimers();

    const identityPromise = resolveWorkforceIdentity({
      randomUserSeed: ONLINE_SEED,
      rngSeedUuid: RNG_SEED_BRAVO,
    });

    await vi.advanceTimersByTimeAsync(600);
    const identity = await identityPromise;

    expect(identity).toMatchObject({ source: 'fallback' });
    expect(identity.traits.length).toBeGreaterThanOrEqual(1);
    expect(identity.traits.length).toBeLessThanOrEqual(2);
    for (const trait of identity.traits) {
      expect(getTraitMetadata(trait.id)).toBeDefined();
    }
  });

  it('uses deterministic pseudodata when the HTTP request fails', async () => {
    const fetchMock = vi.fn<Parameters<typeof fetch>, ReturnType<typeof fetch>>(() =>
      Promise.reject(new Error('network unavailable')),
    );

    vi.stubGlobal('fetch', fetchMock as unknown as typeof fetch);

    const identity = await resolveWorkforceIdentity({
      randomUserSeed: 'unused-seed',
      rngSeedUuid: RNG_SEED_CHARLIE,
    });

    expect(identity).toMatchObject({ source: 'fallback' });
    expect(identity.traits.length).toBeGreaterThanOrEqual(1);
    expect(identity.traits.length).toBeLessThanOrEqual(2);
    for (const trait of identity.traits) {
      expect(getTraitMetadata(trait.id)).toBeDefined();
    }
  });
});

import { describe, expect, it } from 'vitest';

import {
  performMarketHire,
  performMarketScan,
  type PerformMarketHireOptions,
  type PerformMarketScanOptions,
} from '@/backend/src/services/workforce/market';
import { HOURS_PER_DAY } from '@/backend/src/constants/simConstants';
import type {
  EmployeeRole,
  WorkforceMarketState,
  WorkforceState,
} from '@/backend/src/domain/workforce/WorkforceState';
import type { WorkforceConfig } from '@/backend/src/config/workforce';
import { expectDefined } from '../../../util/expectors';

const baseRoles: EmployeeRole[] = [
  {
    id: '00000000-0000-0000-0000-00000000role' as EmployeeRole['id'],
    slug: 'gardener',
    name: 'Gardener',
    coreSkills: [
      { skillKey: 'gardening', minSkill01: 0.4 },
      { skillKey: 'cleanliness', minSkill01: 0.3 },
    ],
  },
];

const baseConfig: WorkforceConfig['market'] = {
  scanCooldown_days: 30,
  poolSize: 4,
  scanCost_cc: 1000,
};

function createMarketState(): WorkforceMarketState {
  return { structures: [] } satisfies WorkforceMarketState;
}

describe('workforce market services', () => {
  it('generates deterministic candidate pools for identical seeds and counters', () => {
    const options: PerformMarketScanOptions = {
      market: createMarketState(),
      config: baseConfig,
      worldSeed: 'seed-123',
      structureId: '00000000-0000-0000-0000-000000000100' as WorkforceState['employees'][number]['assignedStructureId'],
      currentSimHours: 0,
      roles: baseRoles,
    } satisfies PerformMarketScanOptions;

    const first = performMarketScan(options);
    const second = performMarketScan(options);

    expect(first.didScan).toBe(true);
    expect(second.didScan).toBe(true);
    expect(first.pool).toEqual(second.pool);
  });

  it('respects scan cooldowns before generating a new pool', () => {
    const structureId = '00000000-0000-0000-0000-000000000200' as WorkforceState['employees'][number]['assignedStructureId'];
    const initial = performMarketScan({
      market: createMarketState(),
      config: baseConfig,
      worldSeed: 'seed-200',
      structureId,
      currentSimHours: 0,
      roles: baseRoles,
    });

    expect(initial.didScan).toBe(true);
    const market = initial.market;

    const second = performMarketScan({
      market,
      config: baseConfig,
      worldSeed: 'seed-200',
      structureId,
      currentSimHours: 10 * HOURS_PER_DAY,
      roles: baseRoles,
    });

    expect(second.didScan).toBe(false);
    expect(second.market).toEqual(market);
  });

  it('removes hired candidates from the market pool', () => {
    const structureId = '00000000-0000-0000-0000-000000000300' as WorkforceState['employees'][number]['assignedStructureId'];
    const scan = performMarketScan({
      market: createMarketState(),
      config: baseConfig,
      worldSeed: 'seed-300',
      structureId,
      currentSimHours: 0,
      roles: baseRoles,
    });

    const candidate = expectDefined(scan.pool?.[0]);

    const hireOptions: PerformMarketHireOptions = {
      market: scan.market,
      structureId,
      candidateId: candidate.id,
    } satisfies PerformMarketHireOptions;

    const hire = performMarketHire(hireOptions);
    const hiredCandidate = expectDefined(hire.candidate);
    expect(hiredCandidate.id).toBe(candidate.id);

    const structure = expectDefined(hire.market.structures[0]);
    expect(structure.pool).not.toContain(candidate);
  });
});

import { describe, expect, it } from 'vitest';

import {
  applyRaiseIntent,
  createInitialRaiseState,
  RAISE_COOLDOWN_DAYS,
  RAISE_MIN_EMPLOYMENT_DAYS,
} from '@/backend/src/services/workforce/raises';
import { createRng } from '@/backend/src/util/rng';
import type { Employee, WorkforceRaiseIntent } from '@/backend/src/domain/world';
import {
  expectDefined,
  asObject,
  hasKey,
  toNumber,
} from '../../../util/expectors';

function expectObjectProperty<T extends string>(
  source: Record<string, unknown>,
  key: T,
): Record<string, unknown> {
  const hasProperty = hasKey(source, key);
  expect(hasProperty).toBe(true);
  if (!hasProperty) {
    throw new Error(`Expected property "${key}" to be present on object.`);
  }

  return expectDefined(asObject(source[key]));
}

function expectNumberProperty<T extends string>(
  source: Record<string, unknown>,
  key: T,
): number {
  const hasProperty = hasKey(source, key);
  expect(hasProperty).toBe(true);
  if (!hasProperty) {
    throw new Error(`Expected numeric property "${key}" to be present on object.`);
  }

  return toNumber(source[key]);
}

function createEmployee(overrides: Partial<Employee> = {}): Employee {
  const base: Employee = {
    id: '00000000-0000-0000-0000-00000000aaaa' as Employee['id'],
    name: 'Cadence Tester',
    roleId: '00000000-0000-0000-0000-00000000bbbb' as Employee['roleId'],
    rngSeedUuid: '018f43f1-8b44-7b74-b3ce-5fbd7be3c201',
    assignedStructureId: '00000000-0000-0000-0000-00000000cccc' as Employee['assignedStructureId'],
    morale01: 0.7,
    fatigue01: 0.2,
    skills: [{ skillKey: 'analysis', level01: 0.6 }],
    skillTriad: {
      main: { skillKey: 'analysis', level01: 0.6 },
      secondary: [
        { skillKey: 'communication', level01: 0.4 },
        { skillKey: 'operations', level01: 0.3 },
      ],
    },
    traits: [],
    schedule: {
      hoursPerDay: 8,
      overtimeHoursPerDay: 1,
      daysPerWeek: 5,
      shiftStartHour: 8,
    },
    baseRateMultiplier: 1,
    experience: { hoursAccrued: 0, level01: 0 },
    laborMarketFactor: 1,
    timePremiumMultiplier: 1,
    employmentStartDay: 0,
    salaryExpectation_per_h: 18,
    raise: createInitialRaiseState(0),
  } satisfies Employee;

  return { ...base, ...overrides } satisfies Employee;
}

describe('workforce raise cadence', () => {
  it('blocks raise intents before the employment gate', () => {
    const employee = createEmployee();
    const intent: WorkforceRaiseIntent = {
      type: 'workforce.raise.accept',
      employeeId: employee.id,
    };

    const outcome = applyRaiseIntent({ employee, intent, currentSimDay: 90 });

    expect(outcome).toBeNull();
  });

  it('applies raise acceptance and schedules deterministic cooldowns', () => {
    const employee = createEmployee();
    const currentSimDay = RAISE_MIN_EMPLOYMENT_DAYS;
    const intent: WorkforceRaiseIntent = {
      type: 'workforce.raise.accept',
      employeeId: employee.id,
    };

    const outcome = applyRaiseIntent({ employee, intent, currentSimDay });

    expect(outcome).not.toBeNull();
    const result = expectDefined(outcome);
    const resultRecord = expectDefined(asObject(result));

    const moraleDelta = expectNumberProperty(resultRecord, 'moraleDelta01');
    expect(moraleDelta).toBeCloseTo(0.06);

    const rateIncreaseFactor = expectNumberProperty(resultRecord, 'rateIncreaseFactor');
    expect(rateIncreaseFactor).toBeCloseTo(0.05);

    const employeeRecord = expectObjectProperty(resultRecord, 'employee');
    const baseRateMultiplier = expectNumberProperty(employeeRecord, 'baseRateMultiplier');
    expect(baseRateMultiplier).toBeCloseTo(1.05, 5);

    const salaryExpectation = expectNumberProperty(employeeRecord, 'salaryExpectation_per_h');
    expect(salaryExpectation).toBeCloseTo(18.9, 5);

    const morale = expectNumberProperty(employeeRecord, 'morale01');
    expect(morale).toBeCloseTo(0.76, 5);

    const raiseRecord = expectObjectProperty(employeeRecord, 'raise');
    const cadenceSequence = expectNumberProperty(raiseRecord, 'cadenceSequence');
    expect(cadenceSequence).toBe(1);

    const lastDecisionDay = expectNumberProperty(raiseRecord, 'lastDecisionDay');
    expect(lastDecisionDay).toBe(currentSimDay);

    const rng = createRng(employee.rngSeedUuid, 'workforce:raise:1');
    const jitter = Math.round((rng() * 2 - 1) * 45);
    const expectedNext = Math.max(
      currentSimDay + RAISE_MIN_EMPLOYMENT_DAYS,
      currentSimDay + RAISE_COOLDOWN_DAYS + jitter,
    );
    const nextEligibleDay = expectNumberProperty(raiseRecord, 'nextEligibleDay');
    expect(nextEligibleDay).toBe(expectedNext);
  });

  it('resets cadence on ignore and applies morale penalties without rate changes', () => {
    const previous = createEmployee({
      morale01: 0.82,
      baseRateMultiplier: 1.1,
      raise: { cadenceSequence: 1, lastDecisionDay: 200, nextEligibleDay: 380 },
      employmentStartDay: 0,
    });

    const intent: WorkforceRaiseIntent = {
      type: 'workforce.raise.ignore',
      employeeId: previous.id,
    };

    const outcome = applyRaiseIntent({ employee: previous, intent, currentSimDay: 400 });

    expect(outcome).not.toBeNull();
    const result = expectDefined(outcome);
    const resultRecord = expectDefined(asObject(result));

    const employeeRecord = expectObjectProperty(resultRecord, 'employee');
    const baseRateMultiplier = expectNumberProperty(employeeRecord, 'baseRateMultiplier');
    expect(baseRateMultiplier).toBeCloseTo(1.1, 5);

    const morale = expectNumberProperty(employeeRecord, 'morale01');
    const previousMorale = toNumber(previous.morale01);
    expect(morale).toBeLessThan(previousMorale);

    const moraleDelta = expectNumberProperty(resultRecord, 'moraleDelta01');
    expect(moraleDelta).toBeCloseTo(-0.08, 5);

    const raiseRecord = expectObjectProperty(employeeRecord, 'raise');
    const cadenceSequence = expectNumberProperty(raiseRecord, 'cadenceSequence');
    expect(cadenceSequence).toBe(2);

    const lastDecisionDay = expectNumberProperty(raiseRecord, 'lastDecisionDay');
    expect(lastDecisionDay).toBe(400);

    const nextEligibleDay = expectNumberProperty(raiseRecord, 'nextEligibleDay');
    expect(nextEligibleDay).toBeGreaterThanOrEqual(400 + RAISE_MIN_EMPLOYMENT_DAYS);
  });

  it('allows bonus raises with custom rate increases', () => {
    const employee = createEmployee({
      raise: { cadenceSequence: 4, lastDecisionDay: 500, nextEligibleDay: 680 },
      employmentStartDay: 0,
    });

    const intent: WorkforceRaiseIntent = {
      type: 'workforce.raise.bonus',
      employeeId: employee.id,
      rateIncreaseFactor: 0.02,
      bonusAmount_cc: 500,
      moraleBoost01: 0.05,
    };

    const outcome = applyRaiseIntent({ employee, intent, currentSimDay: 700 });

    expect(outcome).not.toBeNull();
    const result = expectDefined(outcome);
    const resultRecord = expectDefined(asObject(result));

    const rateIncreaseFactor = expectNumberProperty(resultRecord, 'rateIncreaseFactor');
    expect(rateIncreaseFactor).toBeCloseTo(0.02, 5);

    const employeeRecord = expectObjectProperty(resultRecord, 'employee');
    const baseRateMultiplier = expectNumberProperty(employeeRecord, 'baseRateMultiplier');
    expect(baseRateMultiplier).toBeCloseTo(1.02, 5);

    const morale = expectNumberProperty(employeeRecord, 'morale01');
    const initialMorale = toNumber(employee.morale01);
    expect(morale).toBeCloseTo(initialMorale + 0.05, 5);

    const raiseRecord = expectObjectProperty(employeeRecord, 'raise');
    const cadenceSequence = expectNumberProperty(raiseRecord, 'cadenceSequence');
    expect(cadenceSequence).toBe(5);

    const nextEligibleDay = expectNumberProperty(raiseRecord, 'nextEligibleDay');
    expect(nextEligibleDay).toBeGreaterThanOrEqual(700 + RAISE_MIN_EMPLOYMENT_DAYS);

    const bonusAmount = expectNumberProperty(resultRecord, 'bonusAmount_cc');
    expect(bonusAmount).toBe(500);
  });
});

import { describe, expect, it, vi } from 'vitest';

import { HOURS_PER_TICK } from '@/backend/src/constants/simConstants';
import { createIrrigationServiceStub } from '@/backend/src/stubs/IrrigationServiceStub';
import { createNutrientBufferStub } from '@/backend/src/stubs/NutrientBufferStub';
import type {
  IrrigationEvent,
  IrrigationServiceInputs,
} from '@/backend/src/domain/interfaces/IIrrigationService';
import type { INutrientBuffer } from '@/backend/src/domain/interfaces/INutrientBuffer';
import type { Uuid } from '@/backend/src/domain/entities';

const MOCK_ZONE_ID = 'zone-123' as Uuid;
const MOCK_PLANT_ID = 'plant-456' as Uuid;
const BASE_BUFFER_STATE: Record<string, number> = { N: 1_000, P: 500, K: 800 };

function createEvent(overrides: Partial<IrrigationEvent> = {}): IrrigationEvent {
  return {
    water_L: 10,
    concentrations_mg_per_L: { N: 50, P: 25, K: 40 },
    targetZoneId: MOCK_ZONE_ID,
    targetPlantId: MOCK_PLANT_ID,
    ...overrides,
  };
}

function createInputs(
  overrides: Partial<IrrigationServiceInputs> = {},
): IrrigationServiceInputs {
  return {
    events: [createEvent()],
    nutrientSource: 'solution',
    ...overrides,
  };
}

describe('IrrigationServiceStub', () => {
  const bufferStub = createNutrientBufferStub();
  const stub = createIrrigationServiceStub(bufferStub);

  it('aggregates a single irrigation event into water and nutrient totals', () => {
    const inputs = createInputs();
    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(result.water_L).toBeCloseTo(10, 5);
    expect(result.nutrients_mg.N).toBeCloseTo(500, 5);
    expect(result.nutrients_mg.P).toBeCloseTo(250, 5);
    expect(result.nutrients_mg.K).toBeCloseTo(400, 5);
  });

  it('sums multiple events including partial nutrient profiles', () => {
    const inputs = createInputs({
      events: [
        createEvent(),
        createEvent({ water_L: 5, concentrations_mg_per_L: { N: 30, Ca: 10 } }),
      ],
    });

    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(result.water_L).toBeCloseTo(15, 5);
    expect(result.nutrients_mg.N).toBeCloseTo(650, 5);
    expect(result.nutrients_mg.P).toBeCloseTo(250, 5);
    expect(result.nutrients_mg.K).toBeCloseTo(400, 5);
    expect(result.nutrients_mg.Ca).toBeCloseTo(50, 5);
  });

  it('delegates nutrient dynamics to the buffer stub', () => {
    const inputs = createInputs({ events: [createEvent({ water_L: 8 })] });
    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(result.leached_mg.N).toBeCloseTo(40, 5);
    expect(result.uptake_mg.N ?? 0).toBe(0);
  });

  it('returns zeros when no events are provided', () => {
    const inputs = createInputs({ events: [] });
    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(result).toEqual({ water_L: 0, nutrients_mg: {}, uptake_mg: {}, leached_mg: {} });
  });

  it('returns zeros when dt is non-positive', () => {
    const inputs = createInputs();
    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, 0);

    expect(result).toEqual({ water_L: 0, nutrients_mg: {}, uptake_mg: {}, leached_mg: {} });
  });

  it('supports injecting a mock nutrient buffer', () => {
    const computeEffect = vi.fn().mockReturnValue({
      uptake_mg: { N: 10 },
      leached_mg: { N: 5 },
      new_buffer_mg: { N: 995 },
    });

    const mockBufferStub: INutrientBuffer = {
      computeEffect,
    };

    const irrigationStub = createIrrigationServiceStub(mockBufferStub);
    const inputs = createInputs();
    const result = irrigationStub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(computeEffect).toHaveBeenCalled();
    expect(result.uptake_mg.N).toBe(10);
    expect(result.leached_mg.N).toBe(5);
  });

  it('leaves nutrient totals empty when concentrations are zero', () => {
    const inputs = createInputs({
      events: [createEvent({ water_L: 5, concentrations_mg_per_L: {} })],
    });

    const result = stub.computeEffect(inputs, BASE_BUFFER_STATE, HOURS_PER_TICK);

    expect(result.water_L).toBe(5);
    expect(result.nutrients_mg).toEqual({});
  });
});
